    for county in counties_tuple:    print(county) #output: Arapahoe, Denver, Jefferson    #Iterate through Dictionarycounties_dict = {"Arapahoe": 422829, "Denver": 463353, "Jefferson": 432438}#to get only the key using for loopfor county in counties_dict:    print(county)    #another way: use the key() methodfor county in counties_dict.keys():    print(county)    #Get the values in dictionaly using for loopfor voters in counties_dict.values():    print(voters)counties_dict = {"Arapahoe": 422829, "Denver": 463353, "Jefferson": 432438}#another way to get the value: use dictionary_name[key]for county in counties_dict:    print(counties_dict[county])    #more way: use get() method to get the values of the keyfor county in counties_dict:    print(counties_dict.get(county))    #Get the Key-Value Pairs of a Dictionary: use items() methodfor county, voters in counties_dict.items():    #print(county, voters)    print(str(county) + " has " + str(voters) + " registered voters.")    #Iterate through a List of Dictionaries#Get each dictionary in a list of dictionaries#to print each dictionary in voting_data:voting_data = [{"county":"Arapahoe", "registered_voters": 422829},                {"county":"Denver", "registered_voters": 463353},                {"county":"Jefferson", "registered_voters": 432438}]for county_dict in voting_data:    print(county_dict)    #use range() to iterate over the list of dictionaries and print the countiesfor i in range(len(voting_data)):    print(voting_data[i])voting_data = [{"county":"Arapahoe", "registered_voters": 422829},                {"county":"Denver", "registered_voters": 463353},                {"county":"Jefferson", "registered_voters": 432438}]    #Get the Values from a List of Dictionaries: need to use nested for loopfor county_dict in voting_data:    for value in county_dict.values():        print(value)        #To retrieve the number of register voters from each dictionary:for county_dict in voting_data:    print(county_dict['registered_voters'])    #To print only the county name:for county_dict in voting_data:    print(county_dict['county'])    #Printing Format: F-formatted string literalmy_votes = int(input("How many votes did you get in the election? "))total_votes = int(input("What is the total votes in the election? "))print(f"I received {my_votes / total_votes * 100}% of the total votes.")#Using F-string with Dictionariescounties_dict = {"Arapahoe": 422829, "Denver": 463353, "Jefferson": 432438}for county, voters in counties_dict.items():    print(f"{county} county has {voters} registered voters.")    #Multiline F stringscandidate_votes = int(input("How many votes did the candidate get in the election? "))total_votes = int(input("What is the total number of votes in the election? "))message_to_candidate = (    f"You received {candidate_votes} number of votes. "    f"The total number of votes in the election was {total_votes}. "    f"You received {candidate_votes / total_votes * 100}% of the total votes.")print(message_to_candidate)#Format Floating-Point Decimals#The general format for a number to format it in an f-string:#f'{value: {width}.{precision}}'candidate_votes = int(input("How many votes did the candidate get in the election? "))total_votes = int(input("What is the total number of votes in the election? "))message_to_candidate = (    f"You received {candidate_votes:,} number of votes. "    f"The total number of votes in the election was {total_votes:,}. "    f"You received {candidate_votes / total_votes * 100:.2f}% of the total votes.")print(message_to_candidate)#Format number in dictionary:counties_dict = {"Arapahoe": 422829, "Denver": 463353, "Jefferson": 432438}for county, voters in counties_dict.items():    print(f"{county} county has {voters:,} registered voters.")    #Another examplevoting_data = [{"county":"Arapahoe", "registered_voters": 422829},                {"county":"Denver", "registered_voters": 463353},                {"county":"Jefferson", "registered_voters": 432438}]for county_dict in voting_data:    print(f"{county_dict['county']} county has {county_dict['registered_voters']:,} registered voters.")    #print(f"{county_dict['registered_voters']:,} registered voters.")        